import React, { Component } from 'react';
import './App.css';

class App extends Component {
  authListener(){
    fire.auth().onAuthStateChanged((user)=>{
      console.log(user);
      if(user){
        this.setState({user});
        localStorage.setItem('user',user.uid);
      }else{
        this.setState({user : NULL});
        localStorage.removeItem('user');
      }

    });
  }
  render() {
    return (
      <div className="App">

      </div>
    );
  }
}

export default App;
